import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";
import { useState, useEffect, useRef } from "react";

function App() {
  const reticleRef = useRef(); // Use useRef to keep track of the reticle
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;
  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;
  let placedObjects = []; // Track placed objects to prevent overlap

  let controller;
  let currentlySelectedObject = null; // Track object near the reticle

  const [isLoading, setIsLoading] = useState(true);
  let loadedModelsCount = 0;


  const [showRotateButtons, setShowRotateButtons] = useState(false); // State to show/hide rotate buttons

  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();
  }, []);

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    controller.addEventListener("select", onSelect);
    scene.add(controller);

    // Initialize the reticle and store it in ref
    reticleRef.current = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticleRef.current.matrixAutoUpdate = false;
    reticleRef.current.visible = false;
    scene.add(reticleRef.current);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
          let model = glb.scene;
          items[i] = model;

          // Increment the loaded models count and check if all are loaded
          loadedModelsCount++;
          if (loadedModelsCount === models.length) {
              setIsLoading(false); // All models are loaded
          }
      }, undefined, function (error) {
          console.error('An error happened while loading the model:', error);
      });
  }
  }

  function onSelect() {
    // Removed from here as we are not placing on select
  }

  function onClicked(e, selectItem, index) {
    itemSelectedIndex = index;

    // Remove image selection from others to indicate unclicked
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      el.classList.remove("clicked");
    }

    // Set image to selected
    e.target.classList.add("clicked");
  }

  function setupFurnitureSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      el.addEventListener("beforexrselect", (e) => {
        e.preventDefault();
        e.stopPropagation();
      });
      el.addEventListener("click", (e) => {
        e.preventDefault();
        e.stopPropagation();
        onClicked(e, items[i], i);
      });
    }
  }

  // Place the object when the button is clicked
  function placeObject() {
    if (reticleRef.current.visible) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      let newPosition = new THREE.Vector3();
      reticleRef.current.matrix.decompose(newPosition, newModel.quaternion, newModel.scale);

      // Check for overlap with existing objects
      const overlap = placedObjects.some((object) => {
        const distance = object.position.distanceTo(newPosition);
        return distance < 1; // Adjust the threshold as necessary
      });

      if (overlap) {
        console.log("Cannot place object: overlap detected.");
        return;
      }

      // Place the object if there's no overlap
      reticleRef.current.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );
      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      scene.add(newModel);
      placedObjects.push(newModel); // Track placed objects

      setShowRotateButtons(false);
    }
  }

  // Rotate the object by a specified angle (left or right)
  function rotateObject(direction) {
    if (currentlySelectedObject) {
      const angle = direction === "left" ? -Math.PI / 16 : Math.PI / 16; // 11.25 degrees per click
      currentlySelectedObject.rotation.y += angle;
    }
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (!reticleRef.current) return; // Check if reticle is defined

    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (!hitTestSourceRequested) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session.requestHitTestSource({ space: referenceSpace }).then(function (source) {
            hitTestSource = source;
          });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);

        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const hitMatrix = hit.getPose(referenceSpace).transform.matrix;

          reticleRef.current.visible = true;
          reticleRef.current.matrix.fromArray(hitMatrix);

          // Decompose the reticle matrix
          const position = new THREE.Vector3();
          const quaternion = new THREE.Quaternion();
          const scale = new THREE.Vector3();

          reticleRef.current.matrix.decompose(position, quaternion, scale);

          // Lock the reticle to the ground level
          position.y = Math.max(position.y, 0.1); // Raise above ground level
          reticleRef.current.position.copy(position);

          // Cast a ray to check for walls
          const direction = new THREE.Vector3(0, -1, 0); // Downwards
          const raycaster = new THREE.Raycaster(position.clone().setY(position.y + 0.1), direction); // Start slightly above the reticle
          const intersects = raycaster.intersectObjects(placedObjects, true); // 'true' to check all descendants

          if (intersects.length > 0) {
            // If the reticle is colliding with an object, adjust its position
            reticleRef.current.position.y = intersects[0].point.y + 0.1; // Adjust to be above the wall
          }

          // Check proximity to placed objects to show rotate buttons
          let foundNearbyObject = false;
          placedObjects.forEach((object) => {
            const distance = object.position.distanceTo(reticleRef.current.position);
            if (distance < 0.5) { // Proximity threshold
              foundNearbyObject = true;
              currentlySelectedObject = object; // Set the object as selected
            }
          });

          // Show/hide rotate buttons based on proximity to placed objects
          setShowRotateButtons(foundNearbyObject);
        } else {
          reticleRef.current.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return (
    <div className="App">
      {/* Place button */}
      <button
        className="place-button"
        onClick={placeObject}
        disabled={itemSelectedIndex < 0} // Disable if no item is selected
      >
        Place Object
      </button>

      {/* Rotate buttons */}
      {showRotateButtons && (
        <>
          <button
            className="rotate-button rotate-left"
            onClick={() => rotateObject("left")}
          >
            ⬅️ Rotate Left
          </button>
          <button
            className="rotate-button rotate-right"
            onClick={() => rotateObject("right")}
          >
            ➡️ Rotate Right
          </button>
        </>
      )}
    </div>
  );
}

export default App;
