import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";

function App() {
  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;

  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;
  let placedObjects = [];

  let controller;

  init();
  setupFurnitureSelection();
  animate();

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    controller.addEventListener("select", onSelect);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    // Create Place Button
    const placeButton = document.createElement("button");
    placeButton.innerText = "Place";
    placeButton.style.position = "absolute";
    placeButton.style.top = "10px";
    placeButton.style.left = "50%";
    placeButton.style.transform = "translateX(-50%)";
    document.body.appendChild(placeButton);
    placeButton.addEventListener("click", placeObject);
  }

  function placeObject() {
    if (reticle.visible) {
      const newModel = items[itemSelectedIndex].clone();
      const position = new THREE.Vector3();
      reticle.matrix.decompose(position, newModel.quaternion, newModel.scale);

      // Check for overlaps
      if (checkOverlap(position)) {
        alert("Cannot place object here. Overlap detected.");
        return;
      }

      // Adjust height to ensure placement on the floor
      newModel.position.copy(position);
      newModel.position.y = 0; // Set to floor level
      newModel.scale.set(modelScaleFactor[itemSelectedIndex], modelScaleFactor[itemSelectedIndex], modelScaleFactor[itemSelectedIndex]);
      scene.add(newModel);
      placedObjects.push(newModel);
      setupObjectInteraction(newModel);
    }
  }

  function checkOverlap(position) {
    for (const obj of placedObjects) {
      const distance = obj.position.distanceTo(position);
      if (distance < 0.2) { // Adjust threshold as needed
        return true; // Overlap detected
      }
    }
    return false; // No overlap
  }

  function setupObjectInteraction(object) {
    object.userData = { isSelected: false };
    object.traverse((child) => {
      if (child.isMesh) {
        child.material.transparent = true;
        child.material.opacity = 0.5; // Highlight
      }
    });

    object.onClick = function () {
      if (!object.userData.isSelected) {
        object.userData.isSelected = true;
        showRotationButtons(object);
      }
    };

    object.addEventListener("click", object.onClick);
  }

  function showRotationButtons(object) {
    const rotateLeftButton = document.createElement("button");
    const rotateRightButton = document.createElement("button");
    rotateLeftButton.innerText = "Rotate Left";
    rotateRightButton.innerText = "Rotate Right";

    rotateLeftButton.style.position = "absolute";
    rotateRightButton.style.position = "absolute";
    rotateLeftButton.style.top = "50px";
    rotateRightButton.style.top = "50px";
    rotateLeftButton.style.left = "10%";
    rotateRightButton.style.left = "60%";

    document.body.appendChild(rotateLeftButton);
    document.body.appendChild(rotateRightButton);

    rotateLeftButton.addEventListener("click", () => {
      object.rotation.y -= Math.PI / 18; // Rotate left
    });
    rotateRightButton.addEventListener("click", () => {
      object.rotation.y += Math.PI / 18; // Rotate right
    });
  }

  function onSelect() {
    if (reticle.visible) {
      // Only select if not placing
      controller.dispatchEvent({ type: "click" });
    }
  }

  const onClicked = (e, selectItem, index) => {
    itemSelectedIndex = index;

    // remove image selection from others to indicate unclicked
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      el.classList.remove("clicked");
    }
    // set image to selected
    e.target.classList.add("clicked");
  };

  function setupFurnitureSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      el.addEventListener("beforexrselect", (e) => {
        e.preventDefault();
        e.stopPropagation();
      });
      el.addEventListener("click", (e) => {
        e.preventDefault();
        e.stopPropagation();
        onClicked(e, items[i], i);
      });
    }
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session.requestHitTestSource({ space: referenceSpace }).then(function (source) {
            hitTestSource = source;
          });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);

        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const hitPose = hit.getPose(referenceSpace);
          const hitNormal = hitPose.transform.orientation;

          if (Math.abs(hitNormal.x) < 0.5 && Math.abs(hitNormal.z) < 0.5) {
            reticle.visible = true;
            reticle.matrix.fromArray(hitPose.transform.matrix);
          } else {
            reticle.visible = false; // Don't show reticle if not horizontal
          }
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return <div className="App"></div>;
}

export default App;
