import React, { useRef, useEffect } from "react";
import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";

function App() {
  // References for mutable variables
  const reticleRef = useRef();
  const placedObjectsRef = useRef([]);
  const itemsRef = useRef([]);
  const controllerRef = useRef();
  const raycasterRef = useRef(new THREE.Raycaster());
  const doubleTapRef = useRef(0);
  const isDraggingRef = useRef(false);
  const selectedObjectRef = useRef(null);
  const previousXRef = useRef(0);

  // References for scene, camera, renderer
  const sceneRef = useRef();
  const cameraRef = useRef();
  const rendererRef = useRef();

  // Model paths and scale factors
  const models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  const modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];

  // Reference to the currently selected item index
  const itemSelectedIndex = useRef(0);

  // Initialize the scene
  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();

    // Cleanup on unmount
    return () => {
      rendererRef.current.dispose();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Initialize function
  const init = () => {
    const myCanvas = document.getElementById("canvas");
    sceneRef.current = new THREE.Scene();
    cameraRef.current = new THREE.PerspectiveCamera(
      70,
      myCanvas.clientWidth / myCanvas.clientHeight,
      0.01,
      20
    );

    // Add hemisphere light
    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    sceneRef.current.add(light);

    // Initialize renderer
    rendererRef.current = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    rendererRef.current.setPixelRatio(window.devicePixelRatio);
    rendererRef.current.setSize(myCanvas.clientWidth, myCanvas.clientHeight);
    rendererRef.current.xr.enabled = true;

    // Add XR estimated light
    const xrLight = new XREstimatedLight(rendererRef.current);
    xrLight.addEventListener("estimationstart", () => {
      sceneRef.current.add(xrLight);
      sceneRef.current.remove(light);
      if (xrLight.environment) {
        sceneRef.current.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      sceneRef.current.add(light);
      sceneRef.current.remove(xrLight);
    });

    // Add AR button
    const arButton = ARButton.createButton(rendererRef.current, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "25%"; // Adjusted to make space for the Place button
    document.body.appendChild(arButton);

    // Load all models
    const loader = new GLTFLoader();
    models.forEach((modelPath, index) => {
      loader.load(
        modelPath,
        function (glb) {
          const model = glb.scene;
          model.traverse((child) => {
            if (child.isMesh) {
              child.castShadow = true;
              child.receiveShadow = true;
            }
          });
          itemsRef.current[index] = model;
          console.log(`Model loaded: ${modelPath}`);
        },
        undefined,
        function (error) {
          console.error(`Error loading model ${modelPath}:`, error);
        }
      );
    });

    // Set up controller
    controllerRef.current = rendererRef.current.xr.getController(0);
    controllerRef.current.addEventListener("select", onSelect);
    sceneRef.current.add(controllerRef.current);

    // Create reticle
    reticleRef.current = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial({
        color: 0x00ff00,
        opacity: 0.75,
        transparent: true,
      })
    );
    reticleRef.current.matrixAutoUpdate = false;
    reticleRef.current.visible = false;
    sceneRef.current.add(reticleRef.current);

