    window.addEventListener("touchstart", onDoubleTap, false); // For mobile
    window.addEventListener("mousedown", onPointerDown, false); // For desktop
    window.addEventListener("mousemove", onPointerMove, false);
    window.addEventListener("mouseup", onPointerUp, false);

    // Handle window resize
    window.addEventListener("resize", onWindowResize, false);
  };

  // Handle window resize
  const onWindowResize = () => {
    const myCanvas = document.getElementById("canvas");
    cameraRef.current.aspect = myCanvas.clientWidth / myCanvas.clientHeight;
    cameraRef.current.updateProjectionMatrix();
    rendererRef.current.setSize(myCanvas.clientWidth, myCanvas.clientHeight);
    console.log("Window resized.");
  };

  // Handle object selection for placement via button
  const handlePlaceButtonClick = () => {
    if (!itemsRef.current[itemSelectedIndex.current]) {
      console.log("No item selected to place.");
      alert("Please select an item to place first.");
      return;
    }

    if (!reticleRef.current.visible) {
      console.log("Reticle not visible. Cannot place object.");
      alert("Please move to a valid placement area.");
      return;
    }

    const newModel = itemsRef.current[itemSelectedIndex.current].clone();
    newModel.visible = true;

    const newPosition = new THREE.Vector3();
    const newQuaternion = new THREE.Quaternion();
    const newScale = new THREE.Vector3();
    reticleRef.current.matrix.decompose(newPosition, newQuaternion, newScale);

    // Set the scale of the new model
    const scaleFactor = modelScaleFactor[itemSelectedIndex.current];
    newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

    // Update the bounding box after scaling
    const newBox = new THREE.Box3().setFromObject(newModel);
    newBox.setFromCenterAndSize(
      newPosition,
      newBox.getSize(newBox.min).multiplyScalar(1) // Adjust multiplier if necessary
    );

    // Check for overlap with existing objects using bounding boxes
    const overlap = placedObjectsRef.current.some((object) => {
      const existingBox = new THREE.Box3().setFromObject(object);
      return existingBox.intersectsBox(newBox);
    });

    if (overlap) {
      console.log("Cannot place object: overlap detected.");
      alert("Cannot place object here. It overlaps with an existing object.");
      return;
    }

    // Place the object if there's no overlap
    newModel.position.copy(newPosition);
    newModel.quaternion.copy(newQuaternion);
    sceneRef.current.add(newModel);
    placedObjectsRef.current.push(newModel); // Track placed objects
    console.log("Object placed via Place button.");
  };

  // Handle object selection via AR controller (no longer used)
  const onSelect = () => {
    // This function is no longer used since placement is handled by the Place button
  };

  // Handle furniture selection from navbar
  const onClicked = (e, selectItem, index) => {
    itemSelectedIndex.current = index;

    // Remove image selection from others to indicate unclicked
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      if (el) {
        el.classList.remove("clicked");
      }
    }

    // Set image to selected
    if (e.target) {
      e.target.classList.add("clicked");
      console.log(`Item selected: ${index}`);
    }
  };

  // Set up furniture selection event listeners
  const setupFurnitureSelection = () => {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      if (el) {
        el.addEventListener("beforexrselect", (e) => {
          e.preventDefault();
          e.stopPropagation();
        });
        el.addEventListener("click", (e) => {
          e.preventDefault();
          e.stopPropagation();
          onClicked(e, itemsRef.current[i], i);
        });
      } else {
        console.warn(`Element #item${i} not found in index.html.`);
      }
    }
  };

  // Detect double-tap using touch events
  const onDoubleTap = (event) => {
    const currentTime = new Date().getTime();
    const tapLength = currentTime - doubleTapRef.current;
    if (tapLength < 300 && tapLength > 0) {
      // Double-tap detected
      handleObjectRemoval(event);
      event.preventDefault();
    }
    doubleTapRef.current = currentTime;
  };

  // Remove object based on tap/click position
  const handleObjectRemoval = (event) => {
    let touchX, touchY;
    if (event.touches && event.touches.length > 0) {
      touchX = event.touches[0].clientX;
      touchY = event.touches[0].clientY;
    } else {
      // For desktop
      touchX = event.clientX;
      touchY = event.clientY;
    }

    const mouse = new THREE.Vector2();
    mouse.x = (touchX / window.innerWidth) * 2 - 1;
    mouse.y = -(touchY / window.innerHeight) * 2 + 1;

    raycasterRef.current.setFromCamera(mouse, cameraRef.current);
    const intersects = raycasterRef.current.intersectObjects(placedObjectsRef.current, true);

    if (intersects.length > 0) {
      const intersected = intersects[0].object;
      const objectToRemove = findTopLevelParent(intersected);
      if (objectToRemove) {
        sceneRef.current.remove(objectToRemove);
        placedObjectsRef.current = placedObjectsRef.current.filter((obj) => obj !== objectToRemove);
        console.log("Object removed on double-tap.");
        alert("Object removed.");
      }
    }
  };

  // Helper function to find the top-level parent in placedObjects
  const findTopLevelParent = (child) => {
    let parent = child;
    while (parent.parent && parent.parent !== sceneRef.current) {
      parent = parent.parent;
    }
    if (placedObjectsRef.current.includes(parent)) {
      return parent;
    }
    return null;
  };

  // Handle pointer down event for dragging-based rotation
  const onPointerDown = (event) => {
    isDraggingRef.current = true;
    previousXRef.current = event.clientX || (event.touches && event.touches[0].clientX);
    console.log("Dragging started.");

    const mouse = new THREE.Vector2();
    if (event.touches && event.touches.length > 0) {
      mouse.x = (event.touches[0].clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.touches[0].clientY / window.innerHeight) * 2 + 1;
    } else {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
    }

    raycasterRef.current.setFromCamera(mouse, cameraRef.current);
    const intersects = raycasterRef.current.intersectObjects(placedObjectsRef.current, true);

    if (intersects.length > 0) {
      selectedObjectRef.current = findTopLevelParent(intersects[0].object);
      if (selectedObjectRef.current) {
        console.log("Object selected for rotation.");
      }
    }
  };

  // Handle pointer move event for dragging-based rotation
  const onPointerMove = (event) => {
    if (isDraggingRef.current && selectedObjectRef.current) {
      const currentX = event.clientX || (event.touches && event.touches[0].clientX);
      const deltaX = currentX - previousXRef.current;
      const rotationSpeed = 0.005; // Adjust rotation speed as needed
      selectedObjectRef.current.rotation.y += deltaX * rotationSpeed;

      previousXRef.current = currentX;
      // console.log(`Rotating object by deltaX: ${deltaX}`);
    }
  };

  // Handle pointer up event to end dragging
  const onPointerUp = () => {
    if (isDraggingRef.current) {
      isDraggingRef.current = false;
      selectedObjectRef.current = null;
      console.log("Dragging ended.");
    }
  };

  // Animation loop
  const animate = () => {
    rendererRef.current.setAnimationLoop(render);
  };

  // Render function
  const render = (timestamp, frame) => {
    if (frame) {
      const referenceSpace = rendererRef.current.xr.getReferenceSpace();
      const session = rendererRef.current.xr.getSession();

      if (!reticleRef.current.hitTestSourceRequested) {
        session
          .requestReferenceSpace("viewer")
          .then(function (referenceSpace) {
            session
              .requestHitTestSource({ space: referenceSpace })
              .then(function (source) {
                reticleRef.current.hitTestSource = source;
              });
          })
          .catch((err) => console.error(err));

        session.addEventListener("end", function () {
          reticleRef.current.hitTestSourceRequested = false;
          reticleRef.current.hitTestSource = null;
        });

        reticleRef.current.hitTestSourceRequested = true;
      }

      if (reticleRef.current.hitTestSource) {
        const hitTestResults = frame.getHitTestResults(reticleRef.current.hitTestSource);

        if (hitTestResults.length > 0) {
          const hit = hitTestResults[0];
          const hitPose = hit.getPose(referenceSpace);
          const hitMatrix = new THREE.Matrix4().fromArray(hitPose.transform.matrix);

          // Extract the normal vector from the hit matrix
          const normal = new THREE.Vector3(0, 1, 0).applyQuaternion(
            new THREE.Quaternion().setFromRotationMatrix(hitMatrix)
          );

          // Define a threshold for how horizontal the plane should be
          const horizontalThreshold = 0.6; // Adjust between 0 (vertical) to 1 (horizontal)

          if (normal.y > horizontalThreshold) {
            // The plane is sufficiently horizontal
            reticleRef.current.visible = true;
            reticleRef.current.matrix.fromArray(hitPose.transform.matrix);
          } else {
            // The plane is not horizontal; hide the reticle
            reticleRef.current.visible = false;
          }
        } else {
          reticleRef.current.visible = false;
        }
      }
    }

    rendererRef.current.render(sceneRef.current, cameraRef.current);
  };

  return (
    <div className="App">
      {/* Place Button Positioned Above the Navbar */}
      <button className="place-button" onClick={handlePlaceButtonClick}>
        Place
      </button>
      {/* No additional elements */}
    </div>
  );
}

export default App;
