import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";

function App() {
  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;

  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;
  let placedModels = [];
  let controller;

  init();
  setupFurnitureSelection();
  animate();

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    controller.addEventListener("select", onSelect);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true, opacity: 0.5 })
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    setupEventListeners();
  }

  function setupFurnitureSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.getElementById(`item${i}`);
      el.addEventListener("click", (e) => {
        e.preventDefault();
        e.stopPropagation();
        itemSelectedIndex = i;
        resetSelection();
        el.classList.add("clicked");
      });
    }
  }

  function resetSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.getElementById(`item${i}`);
      el.classList.remove("clicked");
    }
  }

  function onSelect() {
    if (reticle.visible) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      reticle.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );

      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      if (!checkOverlap(newModel)) {
        placedModels.push(newModel);
        scene.add(newModel);
      } else {
        alert("Warning: Objects cannot overlap!");
      }
    }
  }

  function setupEventListeners() {
    document.addEventListener('dblclick', onDoubleClick);
    controller.addEventListener("selectstart", showRotateButtons);
    controller.addEventListener("selectend", hideRotateButtons);

    const rotateLeftButton = document.getElementById("rotateLeft");
    const rotateRightButton = document.getElementById("rotateRight");

    rotateLeftButton.addEventListener("mousedown", () => rotateObject('left'));
    rotateRightButton.addEventListener("mousedown", () => rotateObject('right'));
    rotateLeftButton.addEventListener("mouseup", stopRotation);
    rotateRightButton.addEventListener("mouseup", stopRotation);
    rotateLeftButton.addEventListener("mouseleave", stopRotation);
    rotateRightButton.addEventListener("mouseleave", stopRotation);
  }

  function onDoubleClick(event) {
    const intersectedObject = getIntersectedObject(event);
    if (intersectedObject) {
      scene.remove(intersectedObject);
      placedModels = placedModels.filter(model => model !== intersectedObject);
    }
  }

  function getIntersectedObject(event) {
    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();
    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);
    const intersects = raycaster.intersectObjects(placedModels);
    return intersects.length > 0 ? intersects[0].object : null;
  }

  function checkOverlap(newModel) {
    const box = new THREE.Box3().setFromObject(newModel);
    return placedModels.some(placed => {
      const placedBox = new THREE.Box3().setFromObject(placed);
      return box.intersectsBox(placedBox);
    });
  }

  function showRotateButtons() {
    document.getElementById("rotateLeft").classList.add("show");
    document.getElementById("rotateRight").classList.add("show");
  }

  function hideRotateButtons() {
    document.getElementById("rotateLeft").classList.remove("show");
    document.getElementById("rotateRight").classList.remove("show");
  }

  let rotationInterval;
  function rotateObject(direction) {
    const intersectedObject = getIntersectedObject();
    if (intersectedObject) {
      const rotationSpeed = 0.01; // Adjust as necessary
      clearInterval(rotationInterval);
      rotationInterval = setInterval(() => {
        if (direction === 'left') {
          intersectedObject.rotation.y -= rotationSpeed;
        } else if (direction === 'right') {
          intersectedObject.rotation.y += rotationSpeed;
        }
      }, 16); // Roughly 60fps
    }
  }

  function stopRotation() {
    clearInterval(rotationInterval);
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session.requestHitTestSource({ space: referenceSpace }).then(function (source) {
            hitTestSource = source;
          });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);
        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          reticle.visible = true;
          reticle.matrix.fromArray(hit.getPose(referenceSpace).transform.matrix);
          reticle.position.y = Math.max(0, reticle.position.y); // Keep on floor
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return (
    <div className="App">
      <div className="navbar">
        {models.map((model, index) => (
          <img
            key={index}
            className="button-image"
            id={`item${index}`}
            src={`item${index}.png`} // Update image source accordingly
            alt={`item ${index}`}
          />
        ))}
      </div>
      <div className="rotate-buttons">
        <button id="rotateLeft" className="button">Rotate Left</button>
        <button id="rotateRight" className="button">Rotate Right</button>
      </div>
    </div>
  );
}

export default App;
