import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";
import { useEffect, useState } from "react";

function App() {
  const [selectedObject, setSelectedObject] = useState(null);
  const [showWarning, setShowWarning] = useState(false);
  const [doubleTapTimeout, setDoubleTapTimeout] = useState(null); // To handle double tap detection

  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;
  let scene, camera, renderer;
  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;
  let placedObjects = [];
  let controller;
  let raycaster = new THREE.Raycaster();
  let rotateInterval = null;

  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();
  }, []);

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    controller.addEventListener("select", onSelect);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    window.addEventListener("click", onSceneClick, false);
  }

  function setupFurnitureSelection() {
    const furnitureButtons = document.querySelectorAll(".button-image");
    furnitureButtons.forEach((button, index) => {
      button.addEventListener("click", () => {
        itemSelectedIndex = index;
        console.log(`Furniture item ${index} selected`);
      });
    });
  }

  function onSelect() {
    if (reticle.visible) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      let newPosition = new THREE.Vector3();
      reticle.matrix.decompose(newPosition, newModel.quaternion, newModel.scale);

      const overlap = placedObjects.some((object) => {
        const distance = object.position.distanceTo(newPosition);
        return distance < 0.5;
      });

      if (overlap) {
        console.log("Cannot place object: overlap detected.");
        setShowWarning(true);
        return;
      }

      reticle.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );
      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      scene.add(newModel);
      placedObjects.push(newModel);
      setSelectedObject(newModel);
    }
  }

  function onSceneClick(event) {
    const mouse = new THREE.Vector2();
    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);
    const intersects = raycaster.intersectObjects(placedObjects);

    if (intersects.length > 0) {
      const tappedObject = intersects[0].object;
      if (selectedObject === tappedObject) {
        // If the same object is tapped twice, remove it
        handleDoubleTap(tappedObject);
      } else {
        // Select the new object
        setSelectedObject(tappedObject);
      }
    } else {
      setSelectedObject(null); // Deselect if tapping outside any objects
    }
  }

  function handleDoubleTap(tappedObject) {
    if (doubleTapTimeout) {
      clearTimeout(doubleTapTimeout);
      setDoubleTapTimeout(null); // Reset timeout
      scene.remove(tappedObject);
      placedObjects = placedObjects.filter((obj) => obj !== tappedObject);
      setSelectedObject(null); // Deselect the removed object
      console.log("Object removed");
    } else {
      const timeout = setTimeout(() => {
        setDoubleTapTimeout(null); // Reset timeout after 300ms
      }, 300);
      setDoubleTapTimeout(timeout);
    }
  }

  function rotateObject(object, angle) {
    object.rotation.y += angle;
  }

  function onRotateLeftPress() {
    rotateInterval = setInterval(() => {
      if (selectedObject) {
        rotateObject(selectedObject, -0.05);
      }
    }, 16); // Rotate smoothly
  }

  function onRotateRightPress() {
    rotateInterval = setInterval(() => {
      if (selectedObject) {
        rotateObject(selectedObject, 0.05);
      }
    }, 16); // Rotate smoothly
  }

  function onRotateRelease() {
    clearInterval(rotateInterval);
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session
            .requestHitTestSource({ space: referenceSpace })
            .then(function (source) {
              hitTestSource = source;
            });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);

        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const hitMatrix = hit.getPose(referenceSpace).transform.matrix;

          reticle.visible = true;
          reticle.matrix.fromArray(hitMatrix);

          const position = new THREE.Vector3();
          const quaternion = new THREE.Quaternion();
          const scale = new THREE.Vector3();

          reticle.matrix.decompose(position, quaternion, scale);
          position.y = 0;

          reticle.position.copy(position);
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return (
    <div className="App">
      {showWarning && <div className="warning">Overlap detected. Cannot place object here.</div>}
      {selectedObject && (
        <>
          <button
            className="rotate-button rotate-left"
            onMouseDown={onRotateLeftPress}
            onMouseUp={onRotateRelease}
            onTouchStart={onRotateLeftPress}
            onTouchEnd={onRotateRelease}
          >
            Rotate Left
          </button>
          <button
            className="rotate-button rotate-right"
            onMouseDown={onRotateRightPress}
            onMouseUp={onRotateRelease}
            onTouchStart={onRotateRightPress}
            onTouchEnd={onRotateRelease}
          >
            Rotate Right
          </button>
        </>
      )}
      <button
        className="place-button"
        onClick={() => {
          // Placeholder for any action when the place button is clicked.
          console.log("Place button clicked");
        }}
      >
        Place
      </button>
    </div>
  );
}

export default App;
