import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";
import { useEffect, useState } from "react";

function App() {
  const [selectedObject, setSelectedObject] = useState(null);
  const [selectedPlacedObject, setSelectedPlacedObject] = useState(null);
  const [placedObjects, setPlacedObjects] = useState([]);
  const [showWarning, setShowWarning] = useState(false);
  const [doubleTapTimeout, setDoubleTapTimeout] = useState(null);

  let scene, camera, renderer, reticle, controller;
  let raycaster = new THREE.Raycaster();
  let rotateInterval = null;

  let items = [];
  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];

  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();
  }, []);

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    document.body.appendChild(arButton);

    // Load models
    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial({ color: 0x0fff00 })
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    window.addEventListener("click", onSceneClick, false);
  }

  function setupFurnitureSelection() {
    const furnitureButtons = document.querySelectorAll(".button-image");
    furnitureButtons.forEach((button, index) => {
      button.addEventListener("click", () => {
        setSelectedObject(index);
      });
    });
  }

  function placeObject() {
    if (reticle.visible && selectedObject !== null) {
      let newModel = items[selectedObject].clone();
      newModel.visible = true;

      let newPosition = new THREE.Vector3();
      reticle.matrix.decompose(newPosition, newModel.quaternion, newModel.scale);

      const overlap = detectOverlap(newPosition);
      if (overlap) {
        setShowWarning(true);
        return;
      }

      newModel.position.copy(newPosition);
      let scaleFactor = modelScaleFactor[selectedObject];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      scene.add(newModel);
      setPlacedObjects([...placedObjects, newModel]);
      setSelectedObject(null);
      reticle.visible = false;
    }
  }

  function detectOverlap(newPosition) {
    return placedObjects.some((object) => {
      const distance = object.position.distanceTo(newPosition);
      return distance < 0.5; // Minimum distance to avoid overlap
    });
  }

  function onSceneClick(event) {
    const mouse = new THREE.Vector2();
    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    raycaster.setFromCamera(mouse, camera);
    const intersects = raycaster.intersectObjects(placedObjects);

    if (intersects.length > 0) {
      const tappedObject = intersects[0].object;
      onSelectObject(tappedObject);
    }
  }

  function onSelectObject(object) {
    if (doubleTapTimeout) {
      clearTimeout(doubleTapTimeout);
      setDoubleTapTimeout(null); // Double-tap detected
      scene.remove(object);
      setPlacedObjects(placedObjects.filter((obj) => obj !== object));
    } else {
      const timeout = setTimeout(() => {
        setDoubleTapTimeout(null);
      }, 300);
      setDoubleTapTimeout(timeout);

      setSelectedPlacedObject(object);
    }
  }

  function handleRotation(direction) {
    if (selectedPlacedObject) {
      rotateInterval = setInterval(() => {
        selectedPlacedObject.rotation.y += direction;
      }, 16); // Rotate smoothly
    }
  }

  function stopRotation() {
    clearInterval(rotateInterval);
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render() {
    renderer.render(scene, camera);
  }

  return (
    <div className="App">
      {showWarning && (
        <div className="warning">Overlap detected. Cannot place object here.</div>
      )}
      {selectedPlacedObject && (
        <>
          <button
            className="rotate-button rotate-left"
            onMouseDown={() => handleRotation(-0.05)}
            onMouseUp={stopRotation}
            onTouchStart={() => handleRotation(-0.05)}
            onTouchEnd={stopRotation}
          >
            Rotate Left
          </button>
          <button
            className="rotate-button rotate-right"
            onMouseDown={() => handleRotation(0.05)}
            onMouseUp={stopRotation}
            onTouchStart={() => handleRotation(0.05)}
            onTouchEnd={stopRotation}
          >
            Rotate Right
          </button>
        </>
      )}
      <button className="place-button" onClick={placeObject}>
        Place
      </button>
    </div>
  );
}

export default App;
