import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";
import { useState, useEffect } from "react";

function App() {
  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;
  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = -1; // Start with an invalid index
  let placedObjects = []; // Track placed objects

  let controller;
  let currentlySelectedObject = null; // Track object near the reticle

  const [showRotateButtons, setShowRotateButtons] = useState(false); // State to show/hide rotate buttons
  const [isModelsLoaded, setIsModelsLoaded] = useState(false); // Track if models are loaded

  // Rotation variables
  let rotationInterval = null;
  const rotationSpeed = Math.PI / 32; // Adjust for speed of rotation

  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();
  }, []);

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(70, window.innerWidth / window.innerHeight, 0.01, 20);

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(window.innerWidth, window.innerHeight); // Use window size
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    // Track model loading completion
    let loadedModelCount = 0;
    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(
        models[i],
        function (glb) {
          let model = glb.scene;
          items[i] = model;

          // Track when all models are loaded
          loadedModelCount++;
          if (loadedModelCount === models.length) {
            setIsModelsLoaded(true); // All models are now loaded
          }
        },
        undefined,
        function (error) {
          console.error(`Error loading model at ${models[i]}`, error);
        }
      );
    }

    controller = renderer.xr.getController(0);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);
  }

  function onClicked(e, index) {
    itemSelectedIndex = index;

    // Remove image selection from others to indicate unclicked
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item${i}`);
      el.classList.remove("clicked");
    }

    // Set image to selected
    e.target.classList.add("clicked");
  }

  function setupFurnitureSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item${i}`);
      el.addEventListener("click", (e) => {
        e.preventDefault();
        onClicked(e, i);
      });
    }
  }

  // Place the object when the button is clicked
  function placeObject() {
    if (reticle.visible && isModelsLoaded && itemSelectedIndex >= 0 && itemSelectedIndex < items.length) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      let newPosition = new THREE.Vector3();
      reticle.matrix.decompose(newPosition, newModel.quaternion, newModel.scale);

      // Check for overlap with existing objects
      const overlap = placedObjects.some((object) => {
        const distance = object.position.distanceTo(newPosition);
        return distance < 1; // Adjust the threshold as necessary
      });

      if (overlap) {
        console.log("Cannot place object: overlap detected.");
        return;
      }

      // Place the object if there's no overlap
      reticle.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );
      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      scene.add(newModel);
      placedObjects.push(newModel); // Track placed objects

      currentlySelectedObject = newModel; // Set the last placed object as selected
      setShowRotateButtons(true); // Show rotate buttons for the last placed object
    }
  }

  function startRotation(direction) {
    if (currentlySelectedObject) {
      const angle = direction === "left" ? -rotationSpeed : rotationSpeed;

      rotationInterval = setInterval(() => {
        currentlySelectedObject.rotation.y += angle;
      }, 100); // Adjust the interval for smoother rotation
    }
  }

  function stopRotation() {
    clearInterval(rotationInterval);
    rotationInterval = null;
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session
            .requestHitTestSource({ space: referenceSpace })
            .then(function (source) {
              hitTestSource = source;
            });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);

        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const hitMatrix = hit.getPose(referenceSpace).transform.matrix;

          reticle.visible = true;
          reticle.matrix.fromArray(hitMatrix);

          const position = new THREE.Vector3();
          const quaternion = new THREE.Quaternion();
          const scale = new THREE.Vector3();

          reticle.matrix.decompose(position, quaternion, scale);
          position.y = 0; // Lock Y-axis

          reticle.position.copy(position);

          // Check if reticle is close to any placed object
          let foundNearbyObject = false;

          placedObjects.forEach((object) => {
            const distance = object.position.distanceTo(reticle.position);
            if (distance < 1) { // Adjust proximity threshold
              foundNearbyObject = true;
              currentlySelectedObject = object; // Set the object as selected
              setShowRotateButtons(true); // Show rotate buttons
            }
          });

          if (!foundNearbyObject) {
            setShowRotateButtons(false); // Hide rotate buttons if no object nearby
            currentlySelectedObject = null;
          }
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return (
    <div className="App">
      {/* Place button */}
      <button
        className="place-button"
        onClick={placeObject}
        disabled={itemSelectedIndex < 0 || !isModelsLoaded} // Disable if no item is selected or models not loaded
      >
        Place Object
      </button>

      {/* Rotate buttons */}
      {showRotateButtons && currentlySelectedObject && (
        <>
          <button
            className="rotate-button rotate-left"
            onMouseDown={() => startRotation("left")}
            onMouseUp={stopRotation}
            onTouchStart={() => startRotation("left")}
            onTouchEnd={stopRotation}
          >
            ⬅️
          </button>
          <button
            className="rotate-button rotate-right"
            onMouseDown={() => startRotation("right")}
            onMouseUp={stopRotation}
            onTouchStart={() => startRotation("right")}
            onTouchEnd={stopRotation}
          >
            ➡️
          </button>
        </>
      )}
    </div>
  );
}

export default App;
