import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";

function App() {
  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;
  let modelScenes = []; // Store the loaded models as scenes for thumbnail generation

  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;

  let controller;
  let lastPlacedObject = null; // Store the last placed object

  // Stack to keep track of placed objects
  let placedObjects = [];
  const MIN_DISTANCE_BETWEEN_OBJECTS = 0.9; // Minimum distance between placed objects

  init();
  setupFurnitureSelection();
  setupRotationControls(); // Set up rotation buttons
  setupRemoveButton(); // Set up remove button
  animate();

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    // AR Button
    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    // Load Models and set up thumbnails
    loadModelsForThumbnails();

    controller = renderer.xr.getController(0);
    scene.add(controller);

    // Reticle
    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    // Add Place Button
    const placeButton = document.createElement("button");
    placeButton.innerText = "Place";
    placeButton.className = "place-button";
    placeButton.style.position = "absolute";
    placeButton.style.bottom = "270px";
    placeButton.style.left = "50%";
    placeButton.style.transform = "translateX(-50%)";
    placeButton.addEventListener("click", onPlaceButtonClicked);
    document.body.appendChild(placeButton);
  }

  // Function to load models for the thumbnails
  function loadModelsForThumbnails() {
    const loader = new GLTFLoader();
    const offscreenRenderer = new THREE.WebGLRenderer({ alpha: true });
    offscreenRenderer.setSize(256, 256); // Thumbnail size

    models.forEach((modelUrl, index) => {
      loader.load(modelUrl, (glb) => {
        const modelScene = glb.scene;
        modelScene.scale.set(0.01, 0.01, 0.01); // Adjust scale for thumbnails
        modelScenes.push(modelScene);

        // Create a scene for rendering the thumbnail
        const thumbnailScene = new THREE.Scene();
        const thumbnailCamera = new THREE.PerspectiveCamera(45, 1, 0.01, 20);
        thumbnailCamera.position.set(0, 0.5, 1.5); // Position the camera for thumbnail

        const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
        light.position.set(0.5, 1, 0.25);
        thumbnailScene.add(light);
        thumbnailScene.add(modelScene);

        // Render the model to the offscreen canvas
        offscreenRenderer.render(thumbnailScene, thumbnailCamera);
        const imgDataUrl = offscreenRenderer.domElement.toDataURL("image/png");

        // Set the image as a background for the navbar item
        const el = document.querySelector(`#item${index}`);
        el.style.backgroundImage = `url(${imgDataUrl})`;
        el.style.backgroundSize = "contain";
        el.style.backgroundRepeat = "no-repeat";
        el.style.backgroundPosition = "center";
      });
    });
  }

  function onPlaceButtonClicked() {
    if (reticle.visible) {
      const newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      // Decompose reticle's position and apply to new model
      reticle.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );

      // Check if the object is far enough from existing objects
      if (canPlaceObject(newModel.position)) {
        let scaleFactor = modelScaleFactor[itemSelectedIndex];
        newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);
        scene.add(newModel);
        placedObjects.push(newModel);
        lastPlacedObject = newModel;
      } else {
        console.log("Object is too close to another object.");
      }
    }
  }

  function canPlaceObject(position) {
    for (let i = 0; i < placedObjects.length; i++) {
      const distance = placedObjects[i].position.distanceTo(position);
      if (distance < MIN_DISTANCE_BETWEEN_OBJECTS) {
        return false; // Too close to another object
      }
    }
    return true;
  }

  function setupFurnitureSelection() {
    models.forEach((_, index) => {
      const el = document.querySelector(`#item${index}`);
      el.addEventListener("beforexrselect", (e) => {
        e.preventDefault();
        e.stopPropagation();
      });
      el.addEventListener("click", (e) => {
        e.preventDefault();
        e.stopPropagation();
        itemSelectedIndex = index;

        // Remove selection state from others
        models.forEach((_, i) => {
          document.querySelector(`#item${i}`).classList.remove("clicked");
        });

        // Highlight the selected item
        el.classList.add("clicked");
      });
    });
  }

  function setupRotationControls() {
    const rotateLeftButton = document.createElement("button");
    rotateLeftButton.innerText = "Rotate Left";
    rotateLeftButton.className = "rotate-left-button";
    rotateLeftButton.style.position = "absolute";
    rotateLeftButton.style.bottom = "200px";
    rotateLeftButton.style.left = "20px";
    rotateLeftButton.addEventListener("click", () => rotateObject(-Math.PI / 8));
    document.body.appendChild(rotateLeftButton);

    const rotateRightButton = document.createElement("button");
    rotateRightButton.innerText = "Rotate Right";
    rotateRightButton.className = "rotate-right-button";
    rotateRightButton.style.position = "absolute";
    rotateRightButton.style.bottom = "200px";
    rotateRightButton.style.right = "20px";
    rotateRightButton.addEventListener("click", () => rotateObject(Math.PI / 8));
    document.body.appendChild(rotateRightButton);
  }

  function rotateObject(angle) {
    if (lastPlacedObject) {
      lastPlacedObject.rotation.y += angle;
    }
  }

  function setupRemoveButton() {
    const removeButton = document.createElement("button");
    removeButton.innerText = "Remove";
    removeButton.className = "remove-button";
    removeButton.style.position = "absolute";
    removeButton.style.bottom = "220px";
    removeButton.style.left = "50%";
    removeButton.style.transform = "translateX(-50%)";
    removeButton.addEventListener("click", removeLastObject);
    document.body.appendChild(removeButton);
  }

  function removeLastObject() {
    if (lastPlacedObject) {
      scene.remove(lastPlacedObject);
      placedObjects.pop();
      lastPlacedObject =
        placedObjects.length > 0 ? placedObjects[placedObjects.length - 1] : null;
    }
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session
            .requestHitTestSource({ space: referenceSpace })
            .then(function (source) {
              hitTestSource = source;
            });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);

        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const matrix = hit.getPose(referenceSpace).transform.matrix;
          const normal = new THREE.Vector3(matrix[4], matrix[5], matrix[6]);
          const angleFromUp = normal.angleTo(new THREE.Vector3(0, 1, 0));
          const verticalThreshold = THREE.MathUtils.degToRad(20);

          if (angleFromUp < verticalThreshold) {
            reticle.visible = true;
            reticle.matrix.fromArray(matrix);
          } else {
            reticle.visible = false;
          }
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  return <div className="App"></div>;
}

export default App;
