import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { XREstimatedLight } from "three/examples/jsm/webxr/XREstimatedLight";

function App() {
  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;

  let scene, camera, renderer;
  let models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb",
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  let modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;
  let placedObjects = new Set(); // Track placed objects
  let controller;

  init();
  setupFurnitureSelection();
  animate();

  function init() {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      70,
      myCanvas.innerWidth / myCanvas.innerHeight,
      0.01,
      20
    );

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "20%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    controller = renderer.xr.getController(0);
    controller.addEventListener("select", onPlace);
    controller.addEventListener("selectstart", onSelectStart);
    controller.addEventListener("selectend", onSelectEnd);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);

    // Add rotate buttons
    createRotateButtons();
  }

  function createRotateButtons() {
    const leftButton = document.createElement("button");
    leftButton.innerText = "Rotate Left";
    leftButton.style.position = "absolute";
    leftButton.style.left = "10px";
    leftButton.style.top = "50%";
    leftButton.style.zIndex = 1;
    leftButton.style.display = "none";
    leftButton.addEventListener("mousedown", () => rotateSelectedObject(-0.1));
    leftButton.addEventListener("mouseup", () => stopRotation());
    leftButton.addEventListener("mouseleave", () => stopRotation());
    document.body.appendChild(leftButton);

    const rightButton = document.createElement("button");
    rightButton.innerText = "Rotate Right";
    rightButton.style.position = "absolute";
    rightButton.style.right = "10px";
    rightButton.style.top = "50%";
    rightButton.style.zIndex = 1;
    rightButton.style.display = "none";
    rightButton.addEventListener("mousedown", () => rotateSelectedObject(0.1));
    rightButton.addEventListener("mouseup", () => stopRotation());
    rightButton.addEventListener("mouseleave", () => stopRotation());
    document.body.appendChild(rightButton);
  }

  let selectedObject = null;
  let rotationInterval;

  function onSelectStart(event) {
    const intersects = getIntersects(event.target);

    if (intersects.length > 0) {
      selectedObject = intersects[0].object;

      // Show rotate buttons
      document.querySelector('button[innerText="Rotate Left"]').style.display = "block";
      document.querySelector('button[innerText="Rotate Right"]').style.display = "block";
    } else {
      selectedObject = null;
      hideRotateButtons();
    }
  }

  function onSelectEnd() {
    hideRotateButtons();
  }

  function hideRotateButtons() {
    document.querySelector('button[innerText="Rotate Left"]').style.display = "none";
    document.querySelector('button[innerText="Rotate Right"]').style.display = "none";
  }

  function stopRotation() {
    clearInterval(rotationInterval);
  }

  function rotateSelectedObject(direction) {
    if (selectedObject) {
      rotationInterval = setInterval(() => {
        selectedObject.rotation.y += direction;
      }, 16); // approximately 60 FPS
    }
  }

  function onPlace() {
    if (reticle.visible) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;

      // Check for overlapping objects
      if (isOverlapping(newModel)) {
        alert("Cannot place object here due to overlap.");
        return;
      }

      reticle.matrix.decompose(
        newModel.position,
        newModel.quaternion,
        newModel.scale
      );

      // Snap model to floor
      newModel.position.y = 0; // Ensure it's placed on the floor
      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);

      // Add model to the scene and track it
      placedObjects.add(newModel);
      scene.add(newModel);
      addDoubleTapRemove(newModel);
    }
  }

  function addDoubleTapRemove(object) {
    let lastTap = 0;
    object.userData.doubleTapHandler = (event) => {
      const currentTime = new Date().getTime();
      if (currentTime - lastTap < 300) {
        // Double tap detected
        scene.remove(object);
        placedObjects.delete(object);
      }
      lastTap = currentTime;
    };

    renderer.domElement.addEventListener("dblclick", object.userData.doubleTapHandler);
  }

  function isOverlapping(newModel) {
    for (const placedObject of placedObjects) {
      if (placedObject !== newModel) {
        const boxA = new THREE.Box3().setFromObject(newModel);
        const boxB = new THREE.Box3().setFromObject(placedObject);
        if (boxA.intersectsBox(boxB)) {
          return true; // Overlap detected
        }
      }
    }
    return false; // No overlap
  }

  function getIntersects(controller) {
    const tempMatrix = new THREE.Matrix4();
    const raycaster = new THREE.Raycaster();

    controller.updateMatrixWorld(); // Ensure the controller's world matrix is updated
    tempMatrix.identity().extractRotation(controller.matrixWorld);
    raycaster.ray.origin.setFromMatrixPosition(controller.matrixWorld);
    raycaster.ray.direction.set(0, 0, -1).applyMatrix4(tempMatrix);

    return raycaster.intersectObjects(Array.from(placedObjects), false);
  }

  function animate() {
    renderer.setAnimationLoop(render);
  }

  function render(timestamp, frame) {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session
            .requestHitTestSource({ space: referenceSpace })
            .then(function (source) {
              hitTestSource = source;
            });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);
        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          const position = hit.getPose(referenceSpace).transform.position;

          reticle.visible = true;
          reticle.matrix.fromArray(hit.getPose(referenceSpace).transform.matrix);
          // Ensure reticle is only visible on the floor
          if (position.y < 0) {
            reticle.visible = false;
          }
        } else {
          reticle.visible = false;
        }
      }
    }

    renderer.render(scene, camera);
  }

  function setupFurnitureSelection() {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);

      // Use a closure to capture the current value of `i`
      (function(index) {
        el.addEventListener("click", (e) => {
          e.preventDefault();
          e.stopPropagation();
          itemSelectedIndex = index; // Set itemSelectedIndex to the current index

          // Remove image selection from others to indicate unclicked
          for (let j = 0; j < models.length; j++) {
            const itemEl = document.querySelector(`#item` + j);
            itemEl.classList.remove("clicked");
          }
          // Set image to selected
          e.target.classList.add("clicked");
        });
      })(i); // Immediately invoke with the current value of `i`
    }
  }


  return <div className="App"></div>;
}

export default App;
